package JV배열;

public class JV배열foreach {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String a[] = {"java","hello","programming"};
		
		for(String i:a)
		{
			System.out.print(i);
		}
		System.out.println();
		int b[] = {1,2,3,4,5};
		for(int i:b)
			System.out.print(i);
	}

}
